/*
  Data Validation Layer - Orchestrator Safety Feature
  Version: 1.1.0 (FIXED)
  Purpose: Validate required fields before calling expensive downstream operations

  This prevents wasting API credits on:
  - AI email template generation (OpenAI/Gemini)
  - Draft email creation (Gmail/Outlook)

  If validation fails, the contact is logged but expensive operations are skipped.

  Estimated cost savings per skipped contact: $0.05-0.15

  CHANGES IN v1.1.0:
  - Fixed field paths: All data is in contactRecord (no jobRecord)
  - Fixed email field: recepientEmail (not email)
  - Changed to runOnceForEachItem mode
*/

// Get contact data from Contact Tracking Workshop output
const contactData = $input.item.json;

// Extract required fields from Contact Tracking Workshop output structure
const contactRecord = contactData.contactRecord || {};

// Define required fields for outreach
const requiredFields = {
  firstName: contactRecord.contactFirstName || '',
  lastName: contactRecord.contactLastName || '',
  email: contactRecord.recepientEmail || '',        // ✅ FIXED: recepientEmail (not email)
  jobTitle: contactRecord.jobTitle || '',           // ✅ FIXED: contactRecord (not jobRecord)
  companyName: contactRecord.companyName || ''      // ✅ FIXED: contactRecord (not jobRecord)
};

// Check which fields are missing or empty
const missingFields = [];
const presentFields = {};

for (const [fieldName, fieldValue] of Object.entries(requiredFields)) {
  if (!fieldValue || fieldValue.trim() === '') {
    missingFields.push(fieldName);
  } else {
    presentFields[fieldName] = fieldValue;
  }
}

// Validation result
const isValid = missingFields.length === 0;

if (!isValid) {
  // VALIDATION FAILED - Skip expensive operations
  return {
    json: {
      validationStatus: 'FAILED',
      validationTimestamp: new Date().toISOString(),
      missingFields: missingFields,
      presentFields: presentFields,
      reason: `Missing required fields: ${missingFields.join(', ')}`,
      skipExpensiveOperations: true,

      // Pass through original data for logging
      originalContactData: contactData,

      // Metadata for tracking
      validationMetadata: {
        validationVersion: '1.1.0',
        validationNode: 'Validate Contact Data Before Expensive Operations',
        validationLocation: 'orchestrator',
        costSavings: {
          aiEmailGeneration: 'saved',
          draftCreation: 'saved',
          estimatedSavingsUSD: 0.10
        }
      }
    }
  };
}

// VALIDATION PASSED - Proceed with expensive operations
return {
  json: {
    validationStatus: 'PASSED',
    validationTimestamp: new Date().toISOString(),
    validatedFields: presentFields,
    skipExpensiveOperations: false,

    // Pass through ALL original data unchanged
    ...contactData,

    // Add validation metadata
    validationMetadata: {
      validationVersion: '1.1.0',
      validationNode: 'Validate Contact Data Before Expensive Operations',
      validationLocation: 'orchestrator',
      allRequiredFieldsPresent: true
    }
  }
};
