// --- The Final, Corrected Code for Your n8n Environment ---

// This will hold the results for every job processed.
const results = [];

// This loop will run once for every job that comes from the previous node.
// CORRECTION: The entire logic is now correctly placed within the function,
// allowing it to access the 'items' array provided by n8n.
for (const item of items) {
  const boundary = 'divider';

  // Get the data for the current job inside the loop.
  const docTitle = item.json.documentTitle;
  const folderId = item.json.folderId;
  let htmlContent = item.json.htmlContent;

  // This formatting correction for the header remains the same.
  htmlContent = htmlContent.replace('IVO DACHEV\nSacramento', 'IVO DACHEV<br>Sacramento');

  const metadata = JSON.stringify({
    name: docTitle,
    mimeType: "application/vnd.google-apps.document",
    parents: [folderId]
  });

  const htmlWithStyles = `
  <!DOCTYPE html>
  <html>
  <head>
    <meta charset="UTF-8">
    <style>
      body { font-family: Arial, sans-serif; line-height: 1.4; font-size: 11pt; }
      h2 { margin-top: 16pt; margin-bottom: 10pt; font-size: 13pt; color: #333333; border-bottom: 1px solid #cccccc; padding-bottom: 3pt; }
      strong { font-size: 13pt; }
      p { margin-bottom: 10pt; }
      ul { margin-top: 0; margin-bottom: 10pt; padding-left: 20pt; }
      li { margin-bottom: 4pt; }
    </style>
  </head>
  <body>
    ${htmlContent}
  </body>
  </html>
  `;

  let body = `--${boundary}\r\n`;
  body += `Content-Type: application/json; charset=UTF-8\r\n\r\n`;
  body += `${metadata}\r\n`;
  body += `--${boundary}\r\n`;
  body += `Content-Type: text/html\r\n\r\n`;
  body += `${htmlWithStyles}\r\n`;
  body += `--${boundary}--`;

  // We add the result for this job to our list, making sure to include
  // the original 'json' data so that 'companyWebsite' is available for the next step.
  results.push({
    json: {
      ...item.json, // This carries all original data forward
      rawData: body,
    }
  });
}

// Return the full list of processed jobs.
return results;

// Get ALL items from the previous node.
const items = $('HTTP Request Apify').all();

// Initialize an array to store the final list of cleaned domains.
let organizationDomainList = [];

// Loop through each item to process its companyWebsite field.
for (const item of items) {
  // Get the company website from the current item in the loop.
  const companyWebsite = item.json.companyWebsite;

  // Process only if companyWebsite is a non-empty string.
  if (typeof companyWebsite === 'string' && companyWebsite.trim() !== '') {

    // Clean the URL by removing prefixes and unwanted characters.
    const cleanedUrlFragment = companyWebsite
      .replace(/^(https?:\/\/)?(www\.)?/, '')
      .replace('@', '');

    // Isolate the domain from any sub-paths.
    const domainPartsArray = cleanedUrlFragment.split('/');
    const cleanedDomain = domainPartsArray[0].trim();

    // MODIFICATION: Add the domain to the list ONLY if it's not empty AND not 'dice.com'.
    if (cleanedDomain && cleanedDomain !== 'dice.com') {
      organizationDomainList.push({ companyWebsite: cleanedDomain });
    }
  }
}

// Return a single item containing the filtered list.
return [{ json: { organizationDomainList: organizationDomainList } }];
