// RESUME GENERATION - INPUT PROCESSING (FIXED FOR DOUBLE NESTING)
// Processes job data and matching results for resume customization
// FIXED: Handles Contact Enrichment Workshop's double nested jobData structure

const inputData = $json;
console.log('ğŸ” Resume Generation Input Debug:');
console.log('   Input keys:', Object.keys(inputData));
console.log('   JobData keys:', inputData.jobData ? Object.keys(inputData.jobData) : 'No jobData');

// CRITICAL FIX: Handle double nested jobData structure from Contact Enrichment
// Contact Enrichment outputs: { jobData: { jobData: {...}, qualityValidation: {...} } }
// We need to extract the INNER jobData object
const jobData = (inputData.jobData && inputData.jobData.jobData) 
  ? inputData.jobData.jobData 
  : (inputData.jobData || inputData);

console.log('   Extracted jobData keys:', Object.keys(jobData));

// CRITICAL FIX: Extract qualityValidation from the outer jobData wrapper
// This contains the Job Matching Workshop's quality analysis
const jobAnalysis = (inputData.jobData && inputData.jobData.qualityValidation)
  ? inputData.jobData.qualityValidation
  : (inputData.jobAnalysis || inputData.jobMatching || null);

console.log('   JobAnalysis keys:', jobAnalysis ? Object.keys(jobAnalysis) : 'No jobAnalysis');

// âœ… CRITICAL FIX: Handle field name differences between workshops
const jobTitle = jobData.jobTitle || jobData.title; // â† FIXED: Now checks both 'jobTitle' and 'title'
const companyName = jobData.company || jobData.companyName; // Handle both field names
const jobLocation = jobData.location || 'Not specified';
const jobDescription = jobData.description || jobData.descriptionText || jobData.descriptionHtml || '';

// FIXED: Validate required inputs with proper field names
if (!jobTitle || !companyName) {
  console.error('âŒ Validation failed:');
  console.error('   Title:', jobTitle);
  console.error('   Company:', companyName);
  console.error('   Available jobData fields:', Object.keys(jobData));
  console.error('   Input structure:', JSON.stringify(inputData, null, 2));
  throw new Error(`Invalid job data: missing required fields (title: ${!!jobTitle}, company: ${!!companyName})`);
}

// FIXED: Handle analysis data structure differences
let analysisScore = 0;
let analysisRecommendation = 'UNKNOWN';
let keyStrengths = [];
let potentialConcerns = [];
let skillsAnalysis = {};
let matchingKeywords = [];

if (jobAnalysis) {
  // Handle both Job Matching Workshop output (qualityValidation) and direct jobAnalysis
  analysisScore = jobAnalysis.qualityScore || jobAnalysis.score || jobAnalysis.compatibilityScore || 75;
  analysisRecommendation = jobAnalysis.recommendation || 'GOOD_MATCH';
  keyStrengths = jobAnalysis.keyStrengths || [];
  potentialConcerns = jobAnalysis.issues || jobAnalysis.potentialConcerns || [];
  skillsAnalysis = jobAnalysis.skillsAnalysis || {};
  matchingKeywords = jobAnalysis.matchingKeywords || [];
} else {
  console.log('âš ï¸ No job analysis data found - using defaults');
  analysisScore = 75; // Default passing score
  analysisRecommendation = 'DEFAULT_MATCH';
}

// Extract key job requirements for resume customization
const jobRequirements = {
  title: jobTitle,
  company: companyName,
  location: jobLocation,
  description: jobDescription,
  requirements: jobData.requirements || '',
  skills: jobData.skills || [],
  experience: jobData.experienceLevel || 'Not specified',
  jobType: jobData.jobType || jobData.employmentType || 'Not specified',
  industry: jobData.industry || 'Not specified'
};

// Extract matching analysis for customization guidance
const matchingGuidance = {
  compatibilityScore: analysisScore,
  score: analysisScore, // Provide both field names for compatibility
  recommendation: analysisRecommendation,
  keyStrengths: keyStrengths,
  potentialConcerns: potentialConcerns,
  skillsAnalysis: skillsAnalysis,
  matchingKeywords: matchingKeywords,
  analysisType: jobAnalysis?.analysisType || 'job-resume-match'
};

// Prepare data for resume customization
const customizationInput = {
  job: jobRequirements,
  matching: matchingGuidance,
  customizationType: 'job-specific-resume-generation',
  timestamp: new Date().toISOString(),
  originalInput: inputData // Keep original for debugging
};

console.log('âœ… Resume Generation Input Processing Success:');
console.log(`   Job: ${jobTitle} at ${companyName}`);
console.log(`   Compatibility Score: ${analysisScore}%`);
console.log(`   Recommendation: ${analysisRecommendation}`);
console.log(`   Key Strengths: ${keyStrengths.length} items`);
console.log(`   Analysis Type: ${jobAnalysis?.analysisType || 'default'}`);

return [{ json: customizationInput }];

// RESUME GENERATION - OUTPUT FORMATTING (v2.0.0)
// Transforms AI Resume Customization output into Merge-compatible format
// FIXED: Passes through contactEnrichment field to Contact Tracking Workshop

// Get the AI Resume Customization output from current node
const aiOutput = $json;

// Parse the JSON from AI Resume Customization (nested in content.parts[0].text)
let resumeData;
try {
  resumeData = JSON.parse(aiOutput.content.parts[0].text);
} catch (parseError) {
  throw new Error(`Failed to parse AI Resume Customization output: ${parseError.message}`);
}

// Get original job data from Job-Resume Input Processing node
const inputProcessingData = $('Job-Resume Input Processing').item.json;
const originalJobData = inputProcessingData.job;

// Get original input data (includes contactEnrichment from Contact Enrichment Workshop)
const originalInput = inputProcessingData.originalInput;

// Validate required data
if (!originalJobData) {
  throw new Error('Missing job data from Job-Resume Input Processing node');
}

if (!originalJobData.title || !originalJobData.company) {
  throw new Error(`Invalid job data: missing required fields (title: ${!!originalJobData.title}, company: ${!!originalJobData.company})`);
}

// Format output for Contact Tracking Workshop
const mergeCompatibleOutput = {
  jobData: {
    title: originalJobData.title,
    company: originalJobData.company,
    location: originalJobData.location,
    description: originalJobData.description,
    requirements: originalJobData.requirements || '',
    skills: originalJobData.skills || [],
    experience: originalJobData.experience || 'Not specified',
    jobType: originalJobData.jobType || 'Not specified',
    industry: originalJobData.industry || 'Not specified'
  },
  resumeGeneration: {
    customizedResume: resumeData.customizedResume || '',
    atsScore: resumeData.optimizationMetrics?.atsScore || 0,
    relevanceScore: resumeData.optimizationMetrics?.relevanceScore || 0,
    keywordDensity: resumeData.optimizationMetrics?.keywordDensity || 'unknown',
    qualityPassed: (resumeData.optimizationMetrics?.atsScore || 0) >= 50,
    customizationSummary: resumeData.customizationSummary || {},
    recommendedActions: resumeData.recommendedActions || [],
    qualityGate: resumeData.qualityGate || {}
  },
  // âœ… CRITICAL FIX: Pass through contactEnrichment from Contact Enrichment Workshop
  contactEnrichment: originalInput?.contactEnrichment || {},
  processingMetadata: {
    workflowId: "resume-generation-merge-ready",
    processedAt: new Date().toISOString(),
    status: "resume_generation_complete",
    version: "2.0.0"
  }
};

// Return formatted output
return [{ json: mergeCompatibleOutput }];
