// OUTREACH TRACKING - INPUT PROCESSING (FIXED DATA STRUCTURE ACCESS)
// Processes contact tracking data for outreach email creation
// FIXED v2.0: Correctly accesses nested contactRecord structure from Contact Tracking Workshop output
// Contact Tracking outputs: { contactRecord: {...}, outreachData: {...}, processingMetadata: {...} }

const inputData = $json;
console.log('🔍 Outreach Input Debug:');
console.log('   Input keys:', Object.keys(inputData));
console.log('   ContactRecord keys:', inputData.contactRecord ? Object.keys(inputData.contactRecord) : 'No contactRecord');
console.log('   OutreachData keys:', inputData.outreachData ? Object.keys(inputData.outreachData) : 'No outreachData');

// ✅ FIXED v2.0: Extract data from Contact Tracking workflow output structure
// Contact Tracking outputs a nested structure with contactRecord, outreachData, processingMetadata
const contactRecord = inputData.contactRecord || {};
const outreachData = inputData.outreachData || {};
const processingMetadata = inputData.processingMetadata || {};

console.log('📋 Contact Record Structure:');
console.log('   Has companyName:', !!contactRecord.companyName);
console.log('   Has jobTitle:', !!contactRecord.jobTitle);
console.log('   Has recepientEmail:', !!contactRecord.recepientEmail);
console.log('   Has contactName:', !!contactRecord.contactName);

// ✅ FIXED v2.0: Extract job information from contactRecord (not from inputData directly)
const jobTitle = contactRecord.jobTitle;
const companyName = contactRecord.companyName;
const jobLocation = contactRecord.jobLocation || 'Not specified';
const jobUrl = contactRecord.jobUrl || '';
const jobDescription = contactRecord.jobDescription || '';

// Validate required inputs
if (!jobTitle || !companyName) {
  console.error('❌ Validation failed:');
  console.error('   JobTitle:', jobTitle);
  console.error('   CompanyName:', companyName);
  console.error('   ContactRecord keys:', Object.keys(contactRecord));
  console.error('   Full input structure:', JSON.stringify(inputData, null, 2).substring(0, 500));
  throw new Error('Invalid job data: missing required fields (jobTitle: ' + !!jobTitle + ', companyName: ' + !!companyName + ')');
}

// ✅ FIXED v3.0: Extract contact information from contactRecord
const contactFirstName = contactRecord.contactFirstName || '';
const contactLastName = contactRecord.contactLastName || '';
const contactName = contactRecord.contactName || `${contactFirstName} ${contactLastName}`.trim() || 'Unknown Contact';
const recepientEmail = contactRecord.recepientEmail || contactRecord.recipientEmail || '';
const contactTitle = contactRecord.contactJobTitle || contactRecord.contactTitle || '';

// ✅ FIXED v2.0: Extract email template information from outreachData
const emailSubject = outreachData.emailSubject || contactRecord.emailSubject || '';
const emailBody = outreachData.emailBody || contactRecord.emailBody || '';

// ✅ FIXED v2.0: Extract duplicate detection fields from contactRecord
const isDuplicate = Boolean(contactRecord.isDuplicate || false);
const duplicateCount = contactRecord.duplicateCount || 0;
const duplicateReason = contactRecord.duplicateReason || '';
const originalApplicationDate = contactRecord.originalApplicationDate || '';
const duplicateDetectedAt = contactRecord.duplicateDetectedAt || '';

// Validate contact email
if (!recepientEmail) {
  throw new Error('Invalid contact data: missing recipient email');
}

// ✅ FIXED v2.0: Extract resume data (if available)
const resumeData = contactRecord.resume || {};
const resumeContentFinal = resumeData.customizedContent || contactRecord.content || '';
const resumeMatchScoreFinal = resumeData.matchScore || contactRecord.resumeMatchScore || 0;
const qualificationScoreFinal = resumeData.qualificationScore || contactRecord.qualificationScore || 0;

// ✅ FIXED v2.0: Create properly structured outreach data
const outreachComponents = {
  job: {
    title: jobTitle,
    company: companyName,
    location: jobLocation,
    jobUrl: jobUrl,
    description: jobDescription
  },
  contact: {
    name: contactName,
    firstName: contactFirstName,
    lastName: contactLastName,
    email: recepientEmail,
    jobTitle: contactTitle,
    company: companyName
  },
  resume: {
    customizedContent: resumeContentFinal,
    matchScore: resumeMatchScoreFinal,
    qualificationScore: qualificationScoreFinal,
    dataSource: resumeContentFinal ? 'CONTACT_RECORD' : 'NONE',
    dataAvailable: !!resumeContentFinal
  },
  email: {
    subject: emailSubject,
    body: emailBody,
    template: 'job-application-outreach',
    estimatedResponseRate: outreachData.estimatedResponseRate || 0
  },
  tracking: {
    dedupeKey: contactRecord.dedupeKey || '',
    trackingId: contactRecord.trackingId || '',
    status: contactRecord.status || 'PREPARED',
    priorityLevel: contactRecord.priorityLevel || 'MEDIUM',
    processedAt: contactRecord.processedAt || new Date().toISOString()
  },
  candidate: {
    name: "Ivo Dachev",
    firstName: "Ivo",
    lastName: "Dachev",
    email: "dachevivo@gmail.com",
    phone: "+1 (650)-222-7923",
    linkedin: "https://www.linkedin.com/in/ivodachev/"
  },
  isDuplicate: isDuplicate,
  duplicateCount: duplicateCount,
  duplicateReason: duplicateReason,
  originalApplicationDate: originalApplicationDate,
  duplicateDetectedAt: duplicateDetectedAt,
  outreachType: 'job-application-email',
  timestamp: new Date().toISOString(),
  originalInput: inputData,
  diagnostics: {
    resumeDataAvailable: !!resumeContentFinal,
    resumeDataSource: resumeContentFinal ? 'CONTACT_RECORD' : 'NONE',
    resumeContentLength: resumeContentFinal.length,
    inputDataKeys: Object.keys(inputData),
    contactRecordKeys: Object.keys(contactRecord),
    outreachDataKeys: Object.keys(outreachData),
    fixVersion: 'v2.0-NESTED-STRUCTURE-FIX'
  }
};

console.log('✅ Outreach Input Processing Success (v2.0):');
console.log('   Job: ' + jobTitle + ' at ' + companyName);
console.log('   Contact: ' + contactName + ' (' + recepientEmail + ')');
console.log('   Contact First Name: ' + contactFirstName);
console.log('   Candidate: ' + outreachComponents.candidate.name);
console.log('   Resume Data Source: ' + outreachComponents.resume.dataSource);
console.log('   Resume Content Length: ' + resumeContentFinal.length + ' characters');
console.log('   Email Subject: ' + emailSubject);
console.log('   Tracking Status: ' + contactRecord.status);
console.log('   🔍 Duplicate Detection: isDuplicate=' + isDuplicate + ', duplicateCount=' + duplicateCount);
console.log('   📋 Processing Path: ' + (isDuplicate ? 'DUPLICATE_DETECTED - Skip Email' : 'NEW_APPLICATION - Generate Email + Resume PDF'));
console.log('   ✅ Data Structure Fix Applied: v2.0');

