// RESUME FILENAME CUSTOMIZER (ENHANCED WITH EMAIL DATA PARSING)
// Renames binary data and parses AI Email data for Draft Gmail node
// FIXED: Parses JSON from AI Email Generation and provides clean data structure

const jobTitle = $('Outreach Input Processing').item.json.job.title;
const companyName = $('Outreach Input Processing').item.json.job.company;
const candidateName = "Ivo Dachev";

// Clean strings for filename
const cleanJobTitle = jobTitle.replace(/[^a-zA-Z0-9]/g, '_');
const cleanCompanyName = companyName.replace(/[^a-zA-Z0-9]/g, '_');
const cleanCandidateName = candidateName.replace(/[^a-zA-Z0-9]/g, '_');

// Generate dynamic filename
const filename = 'Resume_' + cleanCandidateName + '_' + cleanJobTitle + '_' + cleanCompanyName + '.pdf';

console.log('📄 Resume Filename:', filename);

// Get binary data from Google Drive export
const binaryData = $input.item.binary.data;

if (!binaryData) {
  throw new Error('No binary data found from Google Drive export');
}

// ✅ FIXED: Parse AI Email data and extract email content
const aiEmailData = $('AI Email Generation').item.json;

console.log('🔍 AI Email Data Structure:');
console.log('   Has content:', !!aiEmailData.content);
console.log('   Has parts:', !!(aiEmailData.content && aiEmailData.content.parts));
console.log('   Parts length:', aiEmailData.content && aiEmailData.content.parts ? aiEmailData.content.parts.length : 0);

// Parse the JSON string from Gemini response with error handling
let emailSubject = 'Application for ' + jobTitle + ' - ' + candidateName;
let emailBody = 'Dear Hiring Manager,\n\nPlease see my attached resume for the ' + jobTitle + ' position at ' + companyName + '.\n\nSincerely,\n' + candidateName;

try {
  if (aiEmailData.content && aiEmailData.content.parts && aiEmailData.content.parts[0]) {
    const textContent = aiEmailData.content.parts[0].text;
    console.log('📄 Raw AI Response Text (first 200 chars):', textContent.substring(0, 200));

    const emailDataArray = JSON.parse(textContent);
    console.log('✅ JSON Parse Success');
    console.log('   Array length:', emailDataArray.length);

    if (emailDataArray && emailDataArray.length > 0 && emailDataArray[0]) {
      console.log('   Has emailSubject:', !!emailDataArray[0].emailSubject);
      console.log('   Has emailBody:', !!emailDataArray[0].emailBody);

      emailSubject = emailDataArray[0].emailSubject || emailSubject;
      emailBody = emailDataArray[0].emailBody || emailBody;

      console.log('✅ Email Data Extracted Successfully');
    } else {
      console.warn('⚠️ Parsed array is empty or missing element at index 0');
    }
  } else {
    console.warn('⚠️ AI Email data structure is missing content.parts[0]');
  }
} catch (error) {
  console.error('❌ Failed to parse AI Email data:', error.message);
  console.error('   Using fallback email content');
}

console.log('📧 Final Email Subject:', emailSubject);
console.log('📧 Final Email Body Length:', emailBody.length);
console.log('📧 Email Body Preview (first 100 chars):', emailBody.substring(0, 100));

// Validate that we have string values (not undefined)
if (typeof emailSubject !== 'string' || emailSubject.length === 0) {
  console.error('❌ emailSubject is not a valid string, using fallback');
  emailSubject = 'Application for ' + jobTitle + ' - ' + candidateName;
}

if (typeof emailBody !== 'string' || emailBody.length === 0) {
  console.error('❌ emailBody is not a valid string, using fallback');
  emailBody = 'Dear Hiring Manager,\n\nPlease see my attached resume for the ' + jobTitle + ' position at ' + companyName + '.\n\nSincerely,\n' + candidateName;
}

// Return with renamed binary property and CLEAN email data
return {
  json: {
    emailSubject: emailSubject,
    emailBody: emailBody,
    resumeFilename: filename,
    resumeGenerated: true,
    jobTitle: jobTitle,
    companyName: companyName,
    candidateName: candidateName,
    originalAiResponse: aiEmailData  // Keep original for debugging
  },
  binary: {
    resume: {
      data: binaryData.data,
      mimeType: 'application/pdf',
      fileName: filename,
      fileExtension: 'pdf'
    }
  }
};

// ============================================
// DYNAMIC PRIORITY-BASED ACCOUNT SELECTOR
// ============================================
// Purpose: Selects email account using priority-based weighted distribution
// Input: Counter from "Filter to Row 2" + Active accounts from "Filter Active Accounts"
// Output: Selected account + testMode flag + restored binary + restored email data
// Distribution: Tier 1 (≤20 emails/day) - Priority 1: 70%, Priority 2: 20%, Priority 99: 10%
// NEW: Reads testMode flag from Google Sheets for per-account draft/send control
// ============================================

// ============================================
// 📅 WEEKLY CONFIGURATION (MANUAL ADJUSTMENT)
// ============================================
// Change this value every week based on your email volume:
// Week 1 (20-25 emails/week): moduloBase = 10
// Week 2 (40 emails/week): moduloBase = 10
// Week 3 (60 emails/week): moduloBase = 20
// Week 4+ (100+ emails/week): moduloBase = 100
const WEEKLY_MODULO_BASE = 10;  // ← CHANGE THIS VALUE WEEKLY

// Get active accounts from Google Sheets
const activeAccounts = $('Filter Active Accounts').all();
const activeCount = activeAccounts.length;

console.log('📊 Dynamic Priority-Based Account Selector (v7.0 - Test Mode Support)');
console.log('   Active accounts:', activeCount);
console.log('   📅 Weekly Modulo Base:', WEEKLY_MODULO_BASE);

// Get current counter from Google Sheets
const rawCounter = $('Filter to Row 2 (Counter Only)').item.json.counter || 0;
const currentCounter = parseInt(rawCounter, 10) || 0;

// ============================================
// STEP 1: Group accounts by priority level
// ============================================
const priorityGroups = {
  1: [],
  2: [],
  99: []
};

activeAccounts.forEach(account => {
  const priority = account.json.priority || 99;  // Default to Priority 99 if missing
  if (priorityGroups[priority]) {
    priorityGroups[priority].push(account);
  } else {
    // Handle custom priority values by defaulting to Priority 99
    priorityGroups[99].push(account);
  }
});

console.log('   Priority 1 accounts:', priorityGroups[1].length);
console.log('   Priority 2 accounts:', priorityGroups[2].length);
console.log('   Priority 99 accounts:', priorityGroups[99].length);

// ============================================
// STEP 2: Calculate distribution percentages (Tier 1 rules)
// ============================================
const tierDistribution = {
  1: 70,   // Priority 1: 70%
  2: 20,   // Priority 2: 20%
  99: 10   // Priority 99: 10%
};

const accountDistribution = [];

Object.keys(priorityGroups).forEach(priority => {
  const accounts = priorityGroups[priority];
  if (accounts.length > 0) {
    const priorityPercentage = tierDistribution[priority] || 0;
    const perAccountPercentage = priorityPercentage / accounts.length;

    accounts.forEach(account => {
      accountDistribution.push({
        account: account,
        percentage: perAccountPercentage,
        priority: priority
      });
      console.log(`   ${account.json.accountName}: ${perAccountPercentage.toFixed(1)}% (Priority ${priority})`);
    });
  }
});

// ============================================
// STEP 3: Build modulo ranges (ADAPTIVE BASE)
// ============================================
const moduloBase = WEEKLY_MODULO_BASE;  // Use weekly configuration
let currentSlot = 0;

const accountRanges = accountDistribution.map(item => {
  // Calculate slots proportional to percentage
  const slots = Math.round((item.percentage / 100) * moduloBase);
  const startSlot = currentSlot;
  const endSlot = currentSlot + slots - 1;
  currentSlot += slots;

  return {
    account: item.account,
    percentage: item.percentage,
    priority: item.priority,
    startSlot: startSlot,
    endSlot: endSlot,
    slots: slots
  };
});

console.log('   Modulo base:', moduloBase);
accountRanges.forEach(range => {
  console.log(`   ${range.account.json.accountName}: slots ${range.startSlot}-${range.endSlot} (${range.slots} slots)`);
});

// ============================================
// STEP 4: Select account based on counter
// ============================================
const moduloValue = currentCounter % moduloBase;
let selectedAccountData = null;
let selectedRange = null;

for (const range of accountRanges) {
  if (moduloValue >= range.startSlot && moduloValue <= range.endSlot) {
    selectedAccountData = range.account.json;
    selectedRange = range;
    break;
  }
}

if (!selectedAccountData) {
  throw new Error(`❌ ERROR: No account found for modulo value ${moduloValue}. This should never happen.`);
}

const selectedAccount = selectedAccountData.accountName;
const newCounter = currentCounter + 1;  // Increment counter (no modulo reset)

// ============================================
// ✅ NEW: Read testMode flag from selected account
// ============================================
const testMode = selectedAccountData.testMode;
// Handle various boolean representations (TRUE, true, 1, etc.)
const isTestMode = testMode === true || testMode === 'TRUE' || testMode === 'true' || testMode === 1;

// Default to FALSE (production mode) if testMode is missing or invalid
const testModeValue = (testMode === undefined || testMode === null) ? false : isTestMode;

console.log('   Current counter:', currentCounter);
console.log('   Modulo value:', moduloValue);
console.log('   ✅ Selected account:', selectedAccount);
console.log('   Email address:', selectedAccountData.emailAddress);
console.log('   Priority:', selectedRange.priority);
console.log('   Percentage:', selectedRange.percentage.toFixed(1) + '%');
console.log('   🧪 Test Mode:', testModeValue ? 'ENABLED (Create Draft)' : 'DISABLED (Send Email)');

// ============================================
// STEP 5: Restore ALL required data from upstream nodes
// ============================================
const items = $input.all();

return items.map((item, index) => {
  return {
    json: {
      // ✅ Restore email data from Resume Filename Customizer
      emailSubject: $('Resume Filename Customizer').item.json.emailSubject,
      emailBody: $('Resume Filename Customizer').item.json.emailBody,

      // Add counter data for Update Counter node
      counter: newCounter,

      // Add account selection data
      selectedAccount: selectedAccount,  // e.g., 'gmail-dachevivo'
      emailAccount: selectedAccount,
      emailAddress: selectedAccountData.emailAddress,
      credentialName: selectedAccountData.credentialName,
      priority: selectedRange.priority,

      // ✅ NEW: Add testMode flag to output
      testMode: testModeValue,

      // Metadata
      accountSelectionMetadata: {
        itemIndex: index,
        currentCounter: currentCounter,
        newCounter: newCounter,
        moduloValue: moduloValue,
        moduloBase: moduloBase,
        selectedAccount: selectedAccount,
        selectedPriority: selectedRange.priority,
        selectedPercentage: selectedRange.percentage,
        activeAccountCount: activeCount,
        distributionStrategy: 'ADAPTIVE-PRIORITY-BASED-WEIGHTED',
        tierRules: 'P1:70% P2:20% P99:10%',
        weeklyModuloBase: WEEKLY_MODULO_BASE,
        testMode: testModeValue,  // ✅ NEW: Add to metadata for tracking
        testModeSource: testMode === undefined || testMode === null ? 'DEFAULT_FALSE' : 'GOOGLE_SHEETS',
        fixVersion: 'v7.0-TEST-MODE-SUPPORT'
      }
    },
    // ✅ Restore binary from Daily Email Volume Control
    binary: $('Daily Email Volume Control').item.binary,
    pairedItem: {
      item: index
    }
  };
});

// AGGREGATE EMAIL METRICS (4-ACCOUNT SYSTEM)
// Receives input from Gmail API Send OR one of 3 Outlook nodes
// Calculates daily email statistics for dashboard logging
// FIXED v6: Defensive checking for node execution status to prevent "Referenced node doesn't exist" errors

const items = $input.all();

console.log('📊 Aggregate Email Metrics (v6 - 4-Account System with Defensive Checking)');
console.log('   Total items received:', items.length);

// Initialize counters for 4 accounts
let gmailCount = 0;
let outlook1Count = 0;
let outlook2Count = 0;
let outlook3Count = 0;
let totalEmails = 0;
let emailAccount = 'unknown';

// Get the first (and only) item from the email node
const emailItem = items[0];

if (!emailItem) {
  throw new Error('No input received from email node');
}

// ✅ FIX v6: Use defensive checking with try-catch to handle nodes that didn't execute
// N8N throws "Referenced node doesn't exist" error when accessing nodes that exist
// in the workflow but did not execute in the current execution

let gmailApiSendExecuted = false;
try {
  gmailApiSendExecuted = $('Gmail - dachevivo').isExecuted;
} catch (error) {
  gmailApiSendExecuted = false;
}

let outlook1Executed = false;
try {
  outlook1Executed = $('Inbox dachevivo').isExecuted;
} catch (error) {
  outlook1Executed = false;
}

let outlook2Executed = false;
try {
  outlook2Executed = $('Inbox dachevivo2').isExecuted;
} catch (error) {
  outlook2Executed = false;
}

let outlook3Executed = false;
try {
  outlook3Executed = $('Inbox dachevivo3').isExecuted;
} catch (error) {
  outlook3Executed = false;
}

console.log('   Gmail API Send executed:', gmailApiSendExecuted);
console.log('   Inbox Outlook (outlook1) executed:', outlook1Executed);
console.log('   dachevivo2@outlook (outlook2) executed:', outlook2Executed);
console.log('   dachevivo3@outlook.com (outlook3) executed:', outlook3Executed);

// Count the email for the appropriate account
if (gmailApiSendExecuted) {
  gmailCount = 1;
  emailAccount = 'gmail';
  console.log('   ✅ Detected: Gmail email sent via API');
} else if (outlook1Executed) {
  outlook1Count = 1;
  emailAccount = 'outlook1';
  console.log('   ✅ Detected: Outlook #1 draft created (dachevivo@outlook.com)');
} else if (outlook2Executed) {
  outlook2Count = 1;
  emailAccount = 'outlook2';
  console.log('   ✅ Detected: Outlook #2 draft created (dachevivo2@outlook.com)');
} else if (outlook3Executed) {
  outlook3Count = 1;
  emailAccount = 'outlook3';
  console.log('   ✅ Detected: Outlook #3 draft created (dachevivo3@outlook.com)');
} else {
  console.error('   ❌ ERROR: No email node executed!');
  console.error('   This should never happen in the current architecture.');
}

totalEmails = 1; // Always 1 email per execution

// Calculate percentages (will be 100% for whichever account was used)
const gmailPercentage = gmailCount > 0 ? 100 : 0;
const outlook1Percentage = outlook1Count > 0 ? 100 : 0;
const outlook2Percentage = outlook2Count > 0 ? 100 : 0;
const outlook3Percentage = outlook3Count > 0 ? 100 : 0;

// ✅ Convert timestamps to Pacific Time (America/Los_Angeles)
const now = new Date();

// Get Pacific Time string (automatically handles PST/PDT)
const pacificTimeString = now.toLocaleString('en-US', {
  timeZone: 'America/Los_Angeles',
  year: 'numeric',
  month: '2-digit',
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit',
  second: '2-digit',
  hour12: false
});

// Parse the Pacific Time string to get date and time components
const [datePart, timePart] = pacificTimeString.split(', ');
